			$csvFields .= 'roomcalendarmailboxname, ';
			$csvData .= '"' . $roomCalendarMailboxName . '",';
			$calendarStyle = $_POST['calendar-style'];
			$csvFields .= 'calendarstyle,';
			$csvData .= '"' . $calendarStyle . '",';
			if ($calendarStyle == "automatic-processing")
			{
				$editorsListRoomYes = $_POST['editors-list-room-yes'];
				$csvFields .= 'roomadminlist,';
				$csvData .= '"' . $editorsListRoomYes . '",';
				$whoViewCalendarDetails = $_POST['who-view-calendar-details'];
				$csvFields .= 'whoviewcalendardetails,';
				$csvData .= '"' . $whoViewCalendarDetails . '",';
				$defaultPermissionsRoomYes = $_POST['default-permissions-room-yes'];
				$csvFields .= 'defaultpermissions,';
				$csvData .= '"' . $defaultPermissionsRoomYes . '",';
				$whoCanBook = $_POST['who-can-book'];
				$csvFields .= 'whocanbook,';
				$csvData .= '"' . $whoCanBook . '",';
				$customAcceptDeclineMessagesRoom = $_POST['custom-accept-decline-messages-room'];
				$csvFields .= 'customacceptdeclinemessages,';
				$csvData .= '"' . $customAcceptDeclineMessagesRoom . '",';
			}
			else
			{
				$editorsListRoomNo = $_POST['editors-list-room-no'];
				$csvFields .= 'editorslist,';
				$csvData .= '"' . $editorsListRoomNo . '",';
				$authorsListRoom = $_POST['authors-list-room'];
				$csvFields .= 'authorslist,';
				$csvData .= '"' . $authorsListRoom . '",';
				$reviewersListRoom = $_POST['reviewers-list-room'];
				$csvFields .= 'reviewerslist,';
				$csvData .= '"' . $reviewersListRoom . '",';
				$defaultPermissionsRoomNo = $_POST['default-permissions-room-no'];
				$csvFields .= 'defaultpermissions,';
				$csvData .= '"' . $defaultPermissionsRoomNo . '",';
			}
			$needMailboxByCalendar = $_POST['need-mailbox-by-calendar'];
			$csvFields .= 'needmailboxbycalendar,';
			$csvData .= '"' . $needMailboxByCalendar . '",';
			
			$mailResult .= "<strong>Room Building/Number: </strong>" . $roomCalendarMailboxName . "<br><br>";
			$mailResult .= "<strong>Calendar Style: </strong>" . $calendarStyle . "<br><br>";
			if ($calendarStyle == "automatic-processing")
			{
				$mailResult .= "<strong>Room Admin List: </strong>" . nl2br($editorsListRoomYes) . "<br><br>";
				$mailResult .= "<strong>Who Can View Calendar Details: </strong>" . nl2br($whoViewCalendarDetails) . "<br><br>";
				$mailResult .= "<strong>Default Permissions: </strong>" . nl2br($defaultPermissionsRoomYes) . "<br><br>";
				$mailResult .= "<strong>Who Can Book the Room: </strong>" . nl2br($whoCanBook) . "<br><br>";
				$mailResult .= "<strong>Custom Reply Message: </strong>" . nl2br($customAcceptDeclineMessagesRoom) . "<br><br>";
			}
			else //if ($needAutomaticProcessingRoom == "direct-scheduling")
			{
				$mailResult .= "<strong>Editors List: </strong>" . nl2br($editorsListRoomNo) . "<br><br>";
				$mailResult .= "<strong>Authors List: </strong>" . nl2br($authorsListRoom) . "<br><br>";
				$mailResult .= "<strong>Reviewers List: </strong>" . nl2br($reviewersListRoom) . "<br><br>";
				$mailResult .= "<strong>Default Permissions: </strong>" . nl2br($defaultPermissionsRoomNo) . "<br><br>";
			}
			$mailResult .= "<strong>Need Mailbox By: </strong>" . $needMailboxByCalendar . "<br><br>";
		}
			
		
		if ($success)
		{
			//finish up setting up data
			$mailResult .= "<strong>Signing Authority Approval: </strong>" . nl2br($signingAuthorityApproval) . "<br><br>";
			$csvFields .= 'signingauthorityapproval, ';
			$csvData .= '"' . $signingAuthorityApproval . '",';
			$mailResult .= "<strong>Additional Comments: </strong>" . nl2br($additionalComments) . "<br><br>";
			$csvFields .= 'additionalcomments,';
			$csvData .= '"' . $additionalComments . '"';
			
			$csvData = str_replace("\n", ";", $csvData);
			$csvData = str_replace("\r", "", $csvData);
			
			$csvFile .= $csvFields . "\n" . $csvData;
			
			//send email with account info
			$to = 'cn.accounts@helpdesk.oregonstate.edu';
			$subject = 'New CN Account Request - ' . $employeeFirstName . ' ' . $employeeLastName;
			if($type == 'room-calendar'){
				$subject = 'New CN Room Calendar Account Request - ' . $roomCalendarMailboxName . ' (Room Calendar)';
			}
			if($type == 'general-use-calendar'){
				$subject = 'New CN General Use Calendar Account Request - ' . $calendarMailboxName . ' (General Use Calendar)';
			}
			$random_hash = md5(date('r', time()));
			
			$headers =  "MIME-Version: 1.0\r\n";
			$headers .= 'From: "' . $_POST['requestor-name'] . '" <' . $_POST['requestor-mail'] . '>' . "\r\n";
			$headers .= "Content-Type: multipart/mixed; boundary=\"PHP-mixed-".$random_hash."\"";

			$attachment = chunk_split(base64_encode($csvFile));

			$output = 
			"This text will show if your email client does not support MIME. \r\n" .
			"--PHP-mixed-$random_hash\r\n" . 
			"Content-Type: text/html; charset='iso-8859-1'" .
			"Content-Transfer-Encoding: 7bit\r\n\r\n" .
			$mailResult . 
			"\r\n\r\n\r\n" . 
			"--PHP-mixed-$random_hash\r\n" . 
			"Content-Type: text/csv; name=newaccount.csv\r\n" .
			"Content-Transfer-Encoding: base64\r\n" . 
			"Content-Disposition: attachment; file=newaccount.csv\r\n"
			. "\r\n"
			. $attachment 
			. "\r\n"
			. "--PHP-mixed-$random_hash--\r\n";
			
			mail($to, $subject, $output, $headers);
			
			echo "Account Request Sent Succesfully!<br>
			<a href='" . $accountCreateSite . "'>Make another account</a>.
			";
		}
	}
	else
	{
		//redirect to create account page if incorrectly sent to this site
		echo "<script>window.location='" . $accountCreateSite . "';</script>";
	}
?>

